import React from "react";
import axios from "axios";

import { connect } from "react-redux";

import { Button, Navbar, Nav } from "react-bootstrap";

import { Link } from "react-router-dom";

import { BrowserRouter as Router, Route } from "react-router-dom";

// #0
import { setMovies, setUser } from "../../actions/actions";
import MoviesList from "../movies-list/movies-list";

import { LoginView } from "../login-view/login-view";
import { RegistrationView } from "../registration-view/registration-view";
import { MovieView } from "../movie-view/movie-view";
import { MovieCard } from "../movie-card/movie-card";
import { DirectorView } from "../director-view/director-view";
import { GenreView } from "../genre-view/genre-view";
import { ProfileView } from "../profile-view/profile-view";
import { UpdateProfile } from "../update-profile/update-profile";
import { About } from "../Header/About";
import { Contact } from "../Header/Contact";

export class MainView extends React.Component {
  constructor() {
    super();

    this.state = {
      movies: [],
      user: null,
      userInfo: null,
    };
  }

  componentDidMount() {
    let accessToken = localStorage.getItem("token");
    if (accessToken !== null) {
      this.setState({
        user: localStorage.getItem("user"),
      });
      this.getMovies(accessToken), this.getUser(accessToken);
    }
  }
  // getMovies(token) {
  // 	axios
  // 		.get("https://myflix-db.herokuapp.com/movies", {
  // 			headers: { Authorization: `Bearer ${token}` },
  // 		})
  // 		.then((response) => {
  // 			this.setState({
  // 				movies: response.data,
  // 			});
  // 		})
  // 		.catch(function (error) {
  // 			console.log(error);
  // 		});
  // }

  getMovies(token) {
    axios
      .get("https://myflix-db.herokuapp.com/movies", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        // #1
        this.props.setMovies(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  getUser(token) {
    axios
      .get("https://myflix-db.herokuapp.com/users", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        this.props.setUser(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  updateUser(data) {
    this.setState({
      userInfo: data,
    });
    localStorage.setItem("user", data.Username);
  }
  onLoggedIn(authData) {
    console.log(authData);
    this.setState({ user: authData.user.Username, userInfo: authData.user });
    this.props.setUser(authData.user);
    localStorage.setItem("token", authData.token);
    localStorage.setItem("user", authData.user.Username);
    this.getMovies(authData.token);
  }

  onLogout() {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    this.setState({
      user: null,
    });
    window.open("/", "_self");
  }

  render() {
    // #2
    let { movies } = this.props;
    let { user, userInfo, token } = this.state;

    if (!movies) return <div className="main-view" />;

    return (
      <Router basename="/client">
        <Navbar bg="light" expand="lg">
          <Navbar.Brand as={Link} to="/">
            <h1>My Flix</h1>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link as={Link} to="/">
                <h3>Home</h3>
              </Nav.Link>
              <Nav.Link as={Link} to="/user">
                <h3>Profile</h3>
              </Nav.Link>
              <Nav.Link as={Link} to="/about">
                <h3>About</h3>
              </Nav.Link>
              <Nav.Link as={Link} to="/contact">
                <h3>Contact</h3>
              </Nav.Link>
              <Button variant="link" onClick={() => this.onLogout()}>
                <b>Log Out</b>
              </Button>
            </Nav>
          </Navbar.Collapse>
        </Navbar>
        <br></br>
        <br></br>
        <br></br>
        <div className="main-view">
          {/* <Route
						exact
						path="/"
						render={() => {
							if (!user)
								return (
									<LoginView onLoggedIn={(user) => this.onLoggedIn(user)} />
								);
							return movies.map((m) => <MovieCard key={m._id} movie={m} />);
						}}
					/> */}
          <Route
            exact
            path="/"
            render={() => {
              if (!user)
                return (
                  <LoginView onLoggedIn={(user) => this.onLoggedIn(user)} />
                );
              return <MoviesList movies={movies} />;
            }}
          />

          <Route path="/register" render={() => <RegistrationView />} />
          <Route path="/about" render={() => <About />} />
          {/* <Route path="/contact" render={() => <Contact />} /> */}
          <Route path="/contact" component={Contact} />

          <Route
            path="/movies/:movieId"
            render={({ match }) => (
              <MovieView
                movie={movies.find((m) => m._id === match.params.movieId)}
              />
            )}
          />
          <Route
            path="/movies/director/:name"
            render={({ match }) => {
              if (!movies) return <div className="main-view" />;
              return (
                <DirectorView
                  director={
                    movies.find((m) => m.Director.Name === match.params.name)
                      .Director
                  }
                />
              );
            }}
          />
          <Route
            path="/movies/genres/:name"
            render={({ match }) => {
              if (!movies) return <div className="main-view" />;
              return (
                <GenreView
                  genre={
                    movies.find((m) => m.Genre.Name === match.params.name).Genre
                  }
                />
              );
            }}
          />

          <Route
            exact
            path="/user"
            render={() => <ProfileView movies={movies} />}
          />

          {/* <Route path="/user/update" render={() => <UpdateProfile />} /> */}
          <Route
            path="/update/:Username"
            render={() => (
              <UpdateProfile
                userInfo={userInfo}
                user={user}
                token={token}
                updateUser={(data) => this.updateUser(data)}
              />
            )}
          />
        </div>
      </Router>
    );
  }
}

// #3
let mapStateToProps = (state) => {
  return { movies: state.movies };
};

// #4
export default connect(mapStateToProps, { setMovies, setUser })(MainView);
